/*Creating a database called Action Role-Playing Game(ARPG)*/
CREATE DATABASE ARPG;

/*Dropping or deleting table if it exist within the database*/

DROP TABLE IF EXISTS Final_Fantasy_VII;

/*Creating a list of tables within the ARPG database*/

CREATE TABLE Final_Fantasy_VII (
    Developers VARCHAR(50),
    Publishers VARCHAR(50),
    Year_Released INT NOT NULL,
    Systems VARCHAR(50),
    Preceded_By VARCHAR(50),
    Followed_By VARCHAR(50),
    Series VARCHAR(50)
);

DROP TABLE IF EXISTS Characters;

CREATE TABLE Characters (
    "Name" VARCHAR(255),
    Age VARCHAR(255),
    Height VARCHAR(255),
    Birthplace VARCHAR(255),
    DOB VARCHAR(255),
    High_Attributes VARCHAR(255),
    Low_Attributes VARCHAR(255),
    Job_Class VARCHAR(255),
    Weapon VARCHAR(255),
    Limit_Break VARCHAR(255),
    Blood_Type VARCHAR(50),
    Best_Traits VARCHAR(255),
    Worst_Traits VARCHAR(255)
);

DROP TABLE IF EXISTS Limit_Breaks;

CREATE TABLE Limit_Breaks (
    Character VARCHAR(255),
    "Name" VARCHAR(255),
    "Level" INT NOT NULL,
    "Description" VARCHAR(255)
);

DROP TABLE IF EXISTS Attributes;

CREATE TABLE Attributes (
    "Name" VARCHAR(50),
    "Description" VARCHAR(255)
);

DROP TABLE IF EXISTS World_Map_Locations;

CREATE TABLE World_Map_Locations (
    Midgar_Area VARCHAR(255),
    Glasslands_Area VARCHAR(255),
    Junon_Area VARCHAR(255),
    "Corea_&_Saucer_Area" VARCHAR(255),
    "Gongaga_&_Cosmo_Area" VARCHAR(255),
    "Nibel_&_Rocket_Launch_Area" VARCHAR(255),
    Wutai_Area VARCHAR(255),
    Woodlands_Area VARCHAR(255),
    Icicle_Area VARCHAR(255),
    Mideel_Area VARCHAR(255),
    Other_Area VARCHAR(255)
);

DROP TABLE IF EXISTS Weapons;

CREATE TABLE Weapons (
    "Character" VARCHAR(255),
    "Name" VARCHAR(255),
    Attack INT NOT NULL,
    "Attack_%" INT NOT NULL,
    Magic INT NOT NULL,
    Ability_Points VARCHAR(255),
    Materia_Slots VARCHAR(255),
    Materia_Growth VARCHAR(255),
    Special VARCHAR(255),
    Price INT NOT NULL,
    "Location" VARCHAR(255)
);

DROP TABLE IF EXISTS Armor;

CREATE TABLE Armor (
    "Name" VARCHAR(255),
    Defense INT NOT NULL,
    Defense_Rate INT NOT NULL,
    Magic_Defense INT NOT NULL,
    Magic_Defense_Rate INT NOT NULL,
    Ability_Points VARCHAR(100),
    Materia_Slots VARCHAR(255),
    Materia_Growth VARCHAR(255),
    Special VARCHAR(255),
    Price VARCHAR(100),
    "Location" VARCHAR(255)
);

DROP TABLE IF EXISTS Accessory;

CREATE TABLE Accessory (
    "Name" VARCHAR(255),
    "Tier Level" VARCHAR(50),
    Buying_Price VARCHAR(100),
    Selling_Price VARCHAR(100),
    "Description" VARCHAR(255),
    "Location" VARCHAR(255)
);

DROP TABLE IF EXISTS Items;

CREATE TABLE Items(
    "Name" VARCHAR(255),
    "Type" VARCHAR(255),
    Price VARCHAR(255),
    "Target" VARCHAR(255),
    "Description" VARCHAR(255),
    "Location" VARCHAR(255)
);

DROP TABLE IF EXISTS Magic_Materia;

CREATE TABLE Magic_Materia (
    "Name" VARCHAR(255),
    "Type" VARCHAR(255),
    Growth VARCHAR(255),
    Ability_Points INT NOT NULL,
    Spell VARCHAR(255),
    Mana_Points_Cost VARCHAR(255),
    "Attack_%" VARCHAR(255),
    "Power" VARCHAR(255),
    Element VARCHAR(255),
    Reflect VARCHAR(200),
    Strength INT NOT NULL,
    Vitality INT NOT NULL,
    Magic INT NOT NULL,
    Magic_Defense INT NOT NULL,
    Max_Health_Points VARCHAR(150),
    Max_Mana_Points VARCHAR(150),
    Price VARCHAR(150),
    Max_Price INT NOT NULL,
    "Location" VARCHAR(255)
);

DROP TABLE IF EXISTS Summon_Materia;

CREATE TABLE Summon_Materia (
    "Name" VARCHAR(255),
    "Type" VARCHAR(255),
    Magic_Points_Cost INT NOT NULL,
    Element VARCHAR(255),
    Magic INT NOT NULL,
    Magic_Defense INT NOT NULL,
    Max_Health_Points VARCHAR(150),
    Max_Mana_Points VARCHAR(150),
    "Level" VARCHAR(255),
    Ability_Points INT NOT NULL,
    "Description" VARCHAR(255),
    "Location" VARCHAR(255)
);

DROP TABLE IF EXISTS Command_Materia;

CREATE TABLE Command_Materia (
    "Name" VARCHAR(255),
    "Type" VARCHAR(255),
    Strength VARCHAR(255),
    Magic VARCHAR(255),
    Vitality VARCHAR(255),
    Magic_Defense VARCHAR(255),
    Max_Health_Points VARCHAR(150),
    Max_Mana_Points VARCHAR(150),
    Luck VARCHAR(255),
    Price VARCHAR(150),
    "Level" VARCHAR(255),
    "Description" VARCHAR(255),
    Ability_Points INT NOT NULL,
    "Location" VARCHAR(255)
);

DROP TABLE IF EXISTS Enemy_Skills;

CREATE TABLE Enemy_Skills (
    "Name" VARCHAR(255),
    Magic_Points VARCHAR(255),
    Enemies VARCHAR(255),
    "Target" VARCHAR(255),
    "Description" VARCHAR(255),
    "Location" VARCHAR(255)
);

DROP TABLE IF EXISTS Support_Materia;

CREATE TABLE Support_Materia (
    "Name" VARCHAR(255),
    "Type" VARCHAR(255),
    Strength VARCHAR(255),
    Magic VARCHAR(255),
    Vitality VARCHAR(255),
    Magic_Defense VARCHAR(255),
    Max_Health_Points VARCHAR(150),
    Max_Mana_Points VARCHAR(150),
    Luck VARCHAR(255),
    Price VARCHAR(150),
    "Level" VARCHAR(255),
    "Description" VARCHAR(255),
    Ability_Points INT NOT NULL,
    "Location" VARCHAR(255)
);

DROP TABLE IF EXISTS Independent_Materia;

CREATE TABLE Independent_Materia (
    "Name" VARCHAR(255),
    "Type" VARCHAR(255),
    Strength VARCHAR(255),
    Magic VARCHAR(255),
    Vitality VARCHAR(255),
    Magic_Defense VARCHAR(255),
    Max_Health_Points VARCHAR(150),
    Max_Mana_Points VARCHAR(150),
    Luck VARCHAR(255),
    Price VARCHAR(150),
    "Level" VARCHAR(255),
    "Description" VARCHAR(255),
    Ability_Points INT NOT NULL,
    "Location" VARCHAR(255)
);

DROP TABLE IF EXISTS Enemies;

/*Gil = Money*/

CREATE TABLE Enemies (
    "Name" VARCHAR(255),
    "Level" INT NOT NULL,
    "Enemy_Skill" VARCHAR(255),
    Health_Points INT NOT NULL,
    Mana_Points INT NOT NULL,
    Ability_Points INT NOT NULL,
    Experience_Points INT NOT NULL,
    Gil INT NOT NULL,
    Weakness VARCHAR(255),
    Item_Drop VARCHAR(255),
    Item_Steal VARCHAR(255),
