DROP TABLE IF EXISTS Bosses;

CREATE TABLE Bosses (
    "Name" VARCHAR(255),
    "Level" INT NOT NULL,
    "Enemy_Skill" VARCHAR(255),
    Health_Points INT NOT NULL,
